import * as React from 'react';
import JobSearch from '../job_search';
import '../output.css';
import Logo from './seek_logo.png';

export default function Tabs() {
    const [pageIndex, setpageIndex] = React.useState(0)

    const handleTabClick = (index) => {
        
        setpageIndex(index)
      };



    return (
        <div>
            <div class="flex justify-between md:flex md:justify-start md:space-x-10 bg-seekblue">
            
                    <img class="" width='100px' src={Logo} alt="seek logo"/>
          
                
                <div class="hidden md:flex md:space-x-10 md:bg-pink">
                    
                    <span class ="translate-y-1/4 text-lg" onClick={handleTabClick(0)}>Job Search</span>
                    <div class ="translate-y-1/4 text-lg" onClick={handleTabClick(1)}>Profile</div>
                    <div class ="translate-y-1/4 text-lg" onClick={handleTabClick(2)}>Career advice</div>
                    <div class ="translate-y-1/4 text-lg" onClick={handleTabClick(3)}>Explore companies</div>
                </div>
                <div class="right-0 mr-5 translate-y-1/4 text-lg md:hidden">Menu</div>
            </div>
            
        
        </div>

    );
}
