import * as React from 'react';
import '../output.css';


const options = [
    "Acounting", 
    "Administration & Office",
    "Advertising, Arts & Media", 
    "Banking & Financial Services", 
    "Call Centre & Customer Service",
    "CEO & General Management", 
    "Community Services & Development", 
    "Construction", 
    "Consulting & Strategy", 
    "Design & Architecture",
    "Education & Training", 
    "Engineering", 
    "Farming, Animals & Construction", 
    "Government & Defence", 
    "Healthcare & Medical",
    "Hospitality & Tourism", 
    "Human Resources & Recruitment", 
    "Information & Communication Technology", 
    "Insurance & Superannuation",
    "Legal", 
    "Manufacturing, Transport & Logistics", 
    "Marketing & Communications", 
    "Mining, Resources & Energy",
    "Real Estate & Property", 
    "Retail & Consumer Products", 
    "Sales", 
    "Science & Technology", 
    "Self Employment",
    "Sport & Recreation", 
    "Trades & Services"
];


export default function SimpleListMenu () {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [selectedIndex, setSelectedIndex] = React.useState(1);
    /*const open = Boolean(anchorEl);*/
    const [open, setOpen] = React.useState('hidden')
    const handleClickListItem = (event) => {
      setAnchorEl(event.currentTarget);
      setOpen('visible')
    };
  
    const handleMenuItemClick = (event, index) => {
      setSelectedIndex(index);
      setAnchorEl(null);
    };
  
    const handleClose = () => {
      setAnchorEl(null);
      setOpen('hidden')
    };

    return (
        <div class="relative">
            <button class="rounded-lg p-3 bg-white hover:ring-2 ring-blue-500 focus:ring-8 focus:ring-[#7facf5] outline-none" onFocus={handleClickListItem} onBlur={handleClose}>
                {options[selectedIndex]}</button>
            <Dropdown visibility={open}/>

        </div>

    );
}

function Dropdown ({visibility}) {
    const Visible = 
    {
        hidden: 'hidden',
        visible: 'absolute box-content bg-seekblue',
    }

    var listItems = options.map((option, index) => 
        <li class="">
            <button class="p-5">{option}</button>
            </li>
    );

    return (
        <div className={`${Visible[visibility]}`}>
            <ul class="">{listItems}</ul>
        </div>
    )
}
